import React, { useState } from "react";
import './index.css';
import Introduction from './components/Introduction';
import {webDevNDes, digitalArtNGraphDesign} from "./data"
import Project from './components/Project';

function fillProjects() {
   return webDevNDes.map((project) => (
     <Project
       name={project.name}
       date={project.date}
       role={project.role}
       media={project.media}
       description={project.description}
       link={project.link}
     />
   ));

};

function fillDigArtNGraphDesProjects(){
  return digitalArtNGraphDesign.map((project) => (
    <Project
      name={project.name}
      date={project.date}
      role={project.role}
      media={project.media}
      description={project.description}
      link={project.link}
    />
  ));
}

function Menu() {
  const [activeLink, setActiveLink] = useState("home");
  const handleLinkClick = (linkId) => {
    setActiveLink(linkId);
  };

  return (
    <div className="menuDiv">
      <ul className="menuUl">
        <li className="menuLi">
          <a onClick={() => handleLinkClick("home")} className="menuA">
            home
          </a>
        </li>
        <li className="menuLi">
          <a onClick={() => handleLinkClick("webDev&Des")} className="menuA">
            web development & design
          </a>
        </li>
        <li className="menuLi">
          <a
            onClick={() => handleLinkClick("digitalArt&GraphDes")}
            className="menuA"
          >
            digital art & graphic design
          </a>
        </li>
        <li className="menuLi">
          <a onClick={() => handleLinkClick("visualArt")} className="menuA">
            visual art
          </a>
        </li>
      </ul>
      <div>{activeLink === "webDev&Des" &&  fillProjects() }
      {activeLink === "home" &&  <Introduction />}
      {activeLink === "digitalArt&GraphDes" && fillDigArtNGraphDesProjects()}</div>
    </div>
  );
}


function App() {
  fillProjects();
  return (
    <div>
    <Menu />
    </div>
  );
}




export default App;
