import React, {useState} from "react";

const [activeLink, setActiveLink] = useState("home");
const handleLinkClick = (linkId) => {
  setActiveLink(linkId);
};

const Menu =({ activeLink, handleLinkClick }) => {

  return (
    <div className="menuDiv">
      <ul className="menuUl">
        <li className="menuLi">
          <a onClick={() => handleLinkClick("home")} className="menuA">
            home
          </a>
        </li>
        <li className="menuLi">
          <a
            onClick={() => handleLinkClick("webDev&Des")}
            className="menuA"
          >
            web development & design
          </a>
        </li>
        <li className="menuLi">
          <a
            onClick={() => handleLinkClick("digitalArt&GraphDes")}
            className="menuA"

          >
            digital art & graphic design
          </a>
        </li>
        <li className="menuLi">
          <a
            onClick={() => handleLinkClick("visualArt")}
            className="menuA"

          >
            visual art
          </a>
        </li>
      </ul>
    </div>
  );
}

export {Menu, activeLink, handleLinkClick};
