import React, {useState} from "react";

export const activeLink = "home";

function Menu() {
  const [activeLink, setActiveLink] = useState("");

  const handleLinkClick = (linkId) => {
    setActiveLink(linkId);
  };

  return (
    <div className="menuDiv">
      <ul className="menuUl">
        <li className="menuLi">
          <a onClick={() => handleLinkClick("home")} className="menuA" href="#">
            home
          </a>
        </li>
        <li className="menuLi">
          <a
            onClick={() => handleLinkClick("webDev&Des")}
            className="menuA"
            href="#"
          >
            web development & design
          </a>
        </li>
        <li className="menuLi">
          <a
            onClick={() => handleLinkClick("digitalArt&GraphDes")}
            className="menuA"
            href="#"
          >
            digital art & graphic design
          </a>
        </li>
        <li className="menuLi">
          <a
            onClick={() => handleLinkClick("visualArt")}
            className="menuA"
            href="#"
          >
            visual art
          </a>
        </li>
      </ul>
    </div>
  );
}

export default Menu;
